window.addEventListener("DOMContentLoaded",function(){"use strict";document.querySelectorAll(".btn-favorite").forEach(function(e){const t=e.querySelector(".btn-favorite__icon-active"),n=e.querySelector(".btn-favorite__icon-unactive");e.addEventListener("mouseover",function(){t.style.display="none",n.style.display="block"}),e.addEventListener("mouseout",function(){t.style.display="block",n.style.display="none"})})});